using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Data;
using System.Data.SqlClient;
using System.Data.SqlTypes;
using log4net;

namespace JakeKnowsEngineComponent
{
	public class StrategyGetMyPhonesProfile : CStrategyOperation //getPhonesImInProfileData
	{
		public override bool Execute(WSCall ws, DataObjects obj)
		{
			SqlConnection cn = null;
			Logging.LogDebug("Execute");
            try
            {
				int version = ws.GetVersion();
				string Device = ws.GetParameterAs_string("deviceUID"); //Device 
				string Token = ws.GetParameterAs_string("deviceToken"); //Token 
                SqlInt64 IDProfile = ws.GetParameterAs_long("profileId"); //IDProfile 
				bool HasMedia = ws.GetParameterAs_bool("inlineMediaFlag"); //HasMedia 
				bool HasNote = ws.GetParameterAs_bool("inlineNoteFlag"); //HasNote 

				cn = new SqlConnection(JakeKnowsEngine.ConnectionString);
				cn.Open();
				
                Device dev = new Device(cn, obj, Device);                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
                if (dev.IsValid() == true)
                {
                    if (dev.VerifyToken(Token) == true)
                    {
                        SqlInt64 IDProfile_OnDevice = dev.GetProfileID();
                        Profile profile = new Profile(cn, obj, IDProfile);
                        Profile profile_onDevice = new Profile(cn, obj, IDProfile_OnDevice);

                        ws.AddResponse(1, 0);
                    }
                    else
                    {
                        ws.AddResponse(0, -3, "Device token verification failed");
                    }
                }
                else
                {
                    ws.AddResponse(0, -2, "Could not resolve to a valid device.");
                }
            }
            catch (Exception e)
            {
                ws.AddResponse(0, -1, "Exception: " + e.Message);
                log.Error("Exception " + e.Message);
            }
			finally
			{
				if (cn != null)
				{
					if (cn.State != System.Data.ConnectionState.Closed)
						cn.Close();
					cn.Dispose();
				}
			}
            return true;
		}
	}
}
